import React, { useState, useEffect } from 'react';

const MathInput = ({setInputs, inputs, type, difficulty, operation, sameDenoms, mathInputNull, setMathInputNull}) => {
  const [expression, setExpression] = useState('');
  const [inputWidth, setInputWidth] = useState('w-20');
  
  useEffect(() => {
    

    if(mathInputNull){
      setExpression('')
      setMathInputNull(false)

    }
    
    
 }, [difficulty, operation, sameDenoms, mathInputNull])

 const getTextWidth = (text) => {
  const canvas = document.createElement('canvas');
  const context = canvas.getContext('2d');
  context.font = '16px sans-serif'; // Set the font size and style
  return context.measureText(text).width; // Measure the text width
};


  const handleChange = (event) => {
    const inputValue = event.target.value;
    const textWidth = getTextWidth(inputValue);
    setInputWidth(`w-${Math.max(24, Math.min(96, textWidth + 20))} min-w-24 max-w-96`); // Adjust width based on text length
    setExpression(inputValue);
    // Evaluate the expression and update the result
    if(type==='inputNum'){
      try {
        const evalResult = eval(inputValue); // Use of eval can be dangerous, make sure to validate the input
       
       // setResult(isNaN(evalResult) ? null : evalResult); // Check if the result is a valid number
        setInputs({...inputs, inputNum: isNaN(evalResult) ? null : evalResult })
      } catch (error) {
        setInputs({...inputs, inputNum:  null  })
      }
    }

    if(type==='inputDenom'){
      try {
        const evalResult = eval(inputValue); // Use of eval can be dangerous, make sure to validate the input
       
       // setResult(isNaN(evalResult) ? null : evalResult); // Check if the result is a valid number
        setInputs({...inputs, inputDenom: isNaN(evalResult) ? null : evalResult })
      } catch (error) {
        setInputs({...inputs, inputDenom:  null  })
      }
    }
  

  };

  return (
    <div className=''>
      <input
        className={`input   transition-width ${inputWidth}`}
        type="text"
        value={expression}
       
        onChange={handleChange}
      />
    
    </div>
  );
};

export default MathInput;
