import React, { useState, useEffect, useRef, useContext } from "react";
import useShared from "../efractions/useShared";
import CheckModal from "./CheckModal";
import RandomSheets from "./RandomSheets";
import SolutionModal from "./SolutionModal";
import DropdownMulti from "./DropdownMulti";
import MathInput from "./MathInput";
import play from "./../../../images/play.png";
import Fraction from "../efractions/Fraction";
import { MathJaxContext, MathJax } from "better-react-mathjax";
import MathQuestion from "./MathQuestion";

const Arithmetic = () => {
  const btnNextRef = useRef(null);
  const btnFracNextRef = useRef(null);

  const [randomNums, setRandomNums] = useState({
    numerator1: 1,
    denominator1: 1,
    numerator2: 1,
    denominator2: 1,
  });

  const [divisionInputs, setDivisionInputs] = useState({
    numerator1: 0,
    numerator2: 0,
    denominator1: 0,
    denominator2: 0,
    sign: "",
  });

  const [additionInputs, setAdditionInputs] = useState({
    numerator1: 0,
    numerator2: 0,
    denominator1: 0,
    denominator2: 0,
  });

  const [result, setResult] = useState(false);
  const [difficulty, setDifficulty] = useState(1);
  const [difficulty2, setDifficulty2] = useState(1);
  const [operation, setOperation] = useState(3);
  const [sameDenoms, setSameDenoms] = useState(true);
  const [mixOperation, setMixOperation] = useState(0);
  const [efraction, setEfraction] = useState(0);
  const [mathInputNull, setMathInputNull] = useState(false);

  const [inputs, setInputs] = useState({
    inputNum: 0,
    inputDenom: 0,
  });

  const [inputRange, setInputRange] = useState({
    min: 1,
    max: 9,
  });

  const [showRandomSheets, setShowRandomSheets] = useState(false);
  const [totalSheets, setTotalSheets] = useState(0);
  const [showCheckModal, setShowCheckModal] = useState(false);
  const [showSolutionModal, setShowSolutionModal] = useState(false);
  const [prevRandomNums, setPrevRandomNums] = useState();

  function clearAllInputs() {
    setInputs({
      inputNum: null,
      inputDenom: null,
    });

    setAdditionInputs({
      numerator1: "",
      numerator2: "",
      denominator1: "",
      denominator2: "",
    });

    setDivisionInputs({
      numerator1: "",
      numerator2: "",
      denominator1: "",
      denominator2: "",
      sign: "",
    });
  }

  useEffect(() => {
    if (prevRandomNums !== randomNums) {
      handleNext();
      setPrevRandomNums(randomNums);
    }

    clearAllInputs();

    handleNext();
  }, [difficulty, operation, sameDenoms, showRandomSheets, totalSheets]);

  function getRandomNumber(min, max) {
    let randomNumber;
    do {
      randomNumber = Math.floor(Math.random() * (max - min + 1)) + min;
    } while (randomNumber === 0);

    return randomNumber;
  }

  const getRandomOperation = () => {
    let randomNum;
    do {
      randomNum = Math.floor(Math.random() * 4) + 1;
    } while (randomNum === 3);
