                                    {operation === 1 && <h2>+</h2>}
                                    {operation === 2 && <h2>-</h2>}
                                    {operation === 3 && <h2>&times;</h2>}
                                    {operation === 4 && <h2>&divide;</h2>}
                                  </tr>
                                ) : (
                                  <tr>
                                    {mixOperation === 1 && <h2>+</h2>}
                                    {mixOperation === 2 && <h2>-</h2>}
                                    {mixOperation === 3 && <h2>&times;</h2>}
                                    {mixOperation === 4 && <h2>&divide;</h2>}
                                  </tr>
                                )}
                              </tbody>
                            </table>
                          </td>

                          <td className="second-col flex  flex-row px-2 md:px-3">
                            <table>
                              <tbody>
                                <tr>{randomNums.numerator2}</tr>
                                <tr className="line-tr">
                                  <div class="line"></div>
                                </tr>
                                <tr>
                                  {operation < 3 && sameDenoms
                                    ? randomNums.denominator1
                                    : randomNums.denominator2}
                                </tr>
                              </tbody>
                            </table>
                            {operation < 3 && !sameDenoms && (
                              <div className="md:ml-4 flex flex-row">
                                <table className="flex items-center">
                                  <tbody>
                                    <tr className="tr-different-deno ">
                                      <td className="">&times;</td>
                                    </tr>
                                  </tbody>
                                </table>
                                <table className="">
                                  <tbody className="">
                                    <tr className="">
                                      <div className="max-w-4">
                                        <input
                                          value={additionInputs.numerator2}
                                          onChange={(e) =>
                                            setAdditionInputs({
                                              ...additionInputs,
                                              numerator2: e.target.value,
                                            })
                                          }
                                          id="num"
                                          className="input digit-input"
                                        />
                                      </div>
                                    </tr>
                                    <tr className="line-tr">
                                      <div class="line-input"></div>
                                    </tr>
                                    <tr>
                                      <input
                                        value={additionInputs.denominator2}
                                        onChange={(e) =>
                                          setAdditionInputs({
                                            ...additionInputs,
                                            denominator2: e.target.value,
                                          })
                                        }
                                        id="num"
                                        className="input digit-input"
                                      />
                                    </tr>
                                  </tbody>
                                </table>
                              </div>
                            )}
                          </td>

                          <td className="= md:px-3">
                            <tr>=</tr>
                          </td>

                          <td className="inputs md:px-3">
                            <table>
                              <tbody>
                                <tr>
                                  <MathInput
                                    type="inputNum"
                                    setInputs={setInputs}
                                    inputs={inputs}
                                    difficulty={difficulty}
                                    operation={operation}
                                    sameDenoms={sameDenoms}
                                    mathInputNull={mathInputNull}
                                    setMathInputNull={setMathInputNull}
                                  />
                                </tr>
                                <tr className="line-tr">
                                  <div class="line-input"></div>
                                </tr>
                                <tr>
                                  <MathInput
                                    type="inputDenom"
                                    setInputs={setInputs}
                                    inputs={inputs}
                                    difficulty={difficulty}
                                    operation={operation}
                                    sameDenoms={sameDenoms}
                                    mathInputNull={mathInputNull}
                                    setMathInputNull={setMathInputNull}
                                  />
                                </tr>
                              </tbody>
                            </table>
                          </td>
                        </tr>
                      </table>
                    )}

                    <div className="buttons w-full  flex flex-row justify-start mt-14">
                      <button onClick={handleCheck} className="btn-drill">
                        Check
                      </button>

                      <button
                        ref={btnNextRef}
                        onClick={() => {
                          handleNext();
                          setMathInputNull(true);
                        }}
                        className="btn-drill ml-1 md:ml-3"
                      >
                        Next
                      </button>
                    </div>
                  </div>
                ) : (
                  <div>
                    {efraction > 0 && (
                      <Fraction
                        efraction={efraction}
                        difficulty2={difficulty2}
                        btnFracNextRef={btnFracNextRef}
                      />
                    )}
                  </div>
                )}
              </div>
            </div>

            <div className="buttons-div w-100 h-10 md:h-14 bg-gray-200 border-l-4 border-gray-500 mt-8 rounded-md flex items-center justify-start ">
              <button className="flex flex-row items-center px-2 md:px-4">
                <img
                  className="w-6 h-6 md:w-10 md:h-10"
                  src={play}
                  alt="play"
                />
                <span className="font-inter text-[8px] md:text-[16px] ml-1 md:ml-2">
                  View Our Tutorial - Quick tips to complete this exercise
                </span>
              </button>
            </div>
          </div>
        </div>
      </div>
      {/*----------------------- random sheets begin here  ---------------------------------------*/}
      <div className="flex flex-row ">
        <div className=" w-0 sm:w-[5%] md:w-[15%] bg-white"></div>
        <div className=" w-full sm:w-[95%] md:w-[85%] bg-white ">
          <div className="px-[8px] sm:px-[50px] md:px-[20px]  lg:pr-[300px] w-full flex flex-col   pt-2 mt-[30px]">
            <div className="buttons-div w-100 h-12 md:h-16 bg-white  rounded-md flex items-center justify-start px-4 ">
              <div className="flex flex-row justify-center  items-center">
                <label className="text-[10px] sm:text-[14px] md:text-[20px] font-inter text-blue-600 md:mx-2">
                  Generate Sheets:
                </label>
                <input
                  className="input digit-input mx-1"
                  onChange={(e) => handleSetTotalSheets(e.target.value)}
                />
                <div className=" items-end h-4">
                  <label className="text-[6px] sm:text-[8px] md:text-[12px] italic  md:mx-2 text-gray-400">
                    Please provide numbers ranging from 6 to 20.
                  </label>
                </div>
              </div>
            </div>
            <div className=" w-full">
              {totalSheets > 2 && (
                <RandomSheets
                  getRandomNumber={getRandomNumber}
                  showRandomSheets={showRandomSheets}
                  operation={operation}
                  totalSheets={totalSheets}
                  inputRange={inputRange}
                  additionInputs={additionInputs}
                  setAdditionInputs={setAdditionInputs}
                  sameDenoms={sameDenoms}
                  difficulty={difficulty}
                  handleCheck={handleCheck}
                />
              )}
            </div>

            {/* <Temp totalSheets={totalSheets} temp={temp}/> */}

            <CheckModal
              showCheckModal={showCheckModal}
              setShowSolutionModal={setShowSolutionModal}
              setShowCheckModal={setShowCheckModal}
              result={result}
              setInputs={setInputs}
              setAdditionInputs={setAdditionInputs}
              setDivisionInputs={setDivisionInputs}
            />

            <SolutionModal
              showSolutionModal={showSolutionModal}
              setShowSolutionModal={setShowSolutionModal}
              setShowCheckModal={setShowCheckModal}
              randomNums={randomNums}
              operation={operation}
              mixOperation={mixOperation}
              inputs={inputs}
              sameDenoms={sameDenoms}
            />
          </div>
        </div>
      </div>
    </div>
  );
};

export default Arithmetic;
