import React, { useState, useRef } from "react";
import { MathJax, MathJaxContext } from "better-react-mathjax";
import AdvancedCalculator from "./AdvancedCalculator";

const MathQuestion = ({ initialQuestion }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [question, setQuestion] = useState(initialQuestion);
  const textareaRef = useRef(null); // Ref to access the textarea

  const handleEditClick = () => {
    setIsEditing(true);
  };

  const handleSaveClick = () => {
    setIsEditing(false);
  };

  const handleChange = (event) => {
    setQuestion(event.target.value);
  };

  const handleButtonClick = (value) => {
    const textarea = textareaRef.current;
    const cursorPos = textarea.selectionStart;
    const beforeCursor = question.slice(0, cursorPos);
    const afterCursor = question.slice(cursorPos);

    if (value === "Backspace") {
      setQuestion((prevQuestion) => {
        return (
          prevQuestion.slice(0, cursorPos - 1) + prevQuestion.slice(cursorPos)
        );
      });
      return;
    }

    let latexValue = value;
    const specialSymbols = {
      "√": "\\sqrt{}",
      π: "\\pi",
      e: "e",
      "^2": "^2",
      "^3": "^3",
      sin: "\\sin()",
      cos: "\\cos()",
      tan: "\\tan()",
      log: "\\log()",
      ln: "\\ln()",
    };

    if (specialSymbols[value]) {
      latexValue = specialSymbols[value];
    }

    setQuestion((prevQuestion) => {
      const newQuestion = beforeCursor + latexValue + afterCursor;
      return newQuestion;
    });

    // Move cursor to position after inserted value
    textarea.setSelectionRange(
      cursorPos + latexValue.length,
      cursorPos + latexValue.length
    );
  };

  const handleCursorMove = (direction) => {
    const textarea = textareaRef.current;
    const cursorPos = textarea.selectionStart;

    if (direction === "←") {
      textarea.setSelectionRange(
        Math.max(cursorPos - 1, 0),
        Math.max(cursorPos - 1, 0)
      );
    } else if (direction === "→") {
      textarea.setSelectionRange(
        Math.min(cursorPos + 1, question.length),
        Math.min(cursorPos + 1, question.length)
      );
    }
  };

  return (
    <div style={isEditing ? styles.pageShift : null}>
      <MathJaxContext>
        <div style={styles.content}>
          {isEditing ? (
            <div>
              <textarea
                value={question}
                onChange={handleChange}
                rows={4}
                cols={50}
                placeholder="Edit the equation here using LaTeX syntax"
                style={styles.textarea}
                ref={textareaRef}
              />
              <button onClick={handleSaveClick} style={styles.saveButton}>
                Save
              </button>
              <div style={styles.previewContainer}>
                <p>Preview:</p>
                <MathJax>{`\\(${question}\\)`}</MathJax>
              </div>
            </div>
          ) : (
            <div>
              <MathJax>{`\\(${question}\\)`}</MathJax>
              <button onClick={handleEditClick} style={styles.editButton}>
                Edit
              </button>
            </div>
          )}
        </div>
        {isEditing && (
          <AdvancedCalculator
            onButtonClick={handleButtonClick}
            onCursorMove={handleCursorMove}
          />
        )}
      </MathJaxContext>
    </div>
  );
};

const styles = {
  content: {
    marginBottom: "60px", // Ensure content is above the calculator
  },
  pageShift: {
    marginBottom: "80px", // Shift page up by the height of the calculator + margin
    transition: "margin-bottom 0.3s ease", // Smooth transition effect
  },
  textarea: {
    width: "100%",
    boxSizing: "border-box",
  },
  saveButton: {
    marginTop: "10px",
  },
  editButton: {
    marginTop: "10px",
  },
  previewContainer: {
    marginTop: "10px",
    fontWeight: "bold",
  },
};

export default MathQuestion;
