import React, { useState } from "react";
import { MathJax, MathJaxContext } from "better-react-mathjax";
import AdvancedCalculator from "./AdvancedCalculator";

const MathQuestion = ({ initialQuestion }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [question, setQuestion] = useState(initialQuestion);
  const textareaRef = useRef(null);

  const handleEditClick = () => {
    setIsEditing(true);
  };

  const handleSaveClick = () => {
    setIsEditing(false);
  };

  const handleChange = (event) => {
    setQuestion(event.target.value);
  };

  const handleButtonClick = (value) => {
    if (value === "Backspace") {
      setQuestion((prevQuestion) => {
        const cursorPos = textareaRef.current.selectionStart;
        return (
          prevQuestion.slice(0, cursorPos - 1) + prevQuestion.slice(cursorPos)
        );
      });
      return;
    }

    let latexValue = value;

    const specialSymbols = {
      "√": "\\sqrt{}",
      π: "\\pi",
      e: "e",
      "^2": "^2",
      "^3": "^3",
      sin: "\\sin()",
      cos: "\\cos()",
      tan: "\\tan()",
      log: "\\log()",
      ln: "\\ln()",
    };

    if (specialSymbols[value]) {
      latexValue = specialSymbols[value];
    }

    setQuestion((prevQuestion) => {
      const cursorPos = textareaRef.current.selectionStart;
      return (
        prevQuestion.slice(0, cursorPos) +
        latexValue +
        prevQuestion.slice(cursorPos)
      );
    });
  };

  const handleCursorMove = (direction) => {
    const textarea = textareaRef.current;
    const cursorPos = textarea.selectionStart;

    if (direction === "←") {
      textarea.setSelectionRange(
        Math.max(cursorPos - 1, 0),
        Math.max(cursorPos - 1, 0)
      );
    } else if (direction === "→") {
      textarea.setSelectionRange(
        Math.min(cursorPos + 1, question.length),
        Math.min(cursorPos + 1, question.length)
      );
    }
  };

  return (
    <div>
      <MathJaxContext>
        {isEditing ? (
          <div>
            <textarea
              value={question}
              onChange={handleChange}
              rows={4}
              cols={50}
              placeholder="Edit the equation here using LaTeX syntax"
            />
            <button onClick={handleSaveClick}>Save</button>
            <AdvancedCalculator onButtonClick={handleButtonClick} />
            <div style={{ marginTop: "10px", fontWeight: "bold" }}>
              <p>Preview:</p>
              <MathJax>{`\\(${question}\\)`}</MathJax>
            </div>
          </div>
        ) : (
          <div>
            <MathJax>{`\\(${question}\\)`}</MathJax>
            <button onClick={handleEditClick}>Edit</button>
          </div>
        )}
      </MathJaxContext>
    </div>
  );
};

export default MathQuestion;
